// テキスト「繰り返し」
function setup() {
  createCanvas(100,100);
  background(225);
  ellipse(widrh/2,height/2,10);
    nofill();
  fill(0);
  let x = 0;
  while(x < 100){ // 四角を描く座標が描画範囲内であれば続ける
    rect(x, x, 20, 20);
    x = x + 20; // 繰り返し1回ごとに場所を動かす
  }
}

