  const blue = color(0, 51, 160);
  const white =color(255,255,253);
  createCanvas(270, 180);
  // noStroke();
  background(255);

  let d = height / 9; // 縞1本の太さ

  for(let i = 0; i < 9; i++){
    // BLANK[1] (hint: 縞の色を交互に変えるには2で割った余りを使おう)
    if((i%2)===1){
      fill(white);
      rect(0, i * d, width, (i + 1) * d);
    }else{
      fill(blue);
      rect(0, i * d, width, (i + 1) * d);
    }
  }

  fill(blue);
  let size = d * 5;
  rect(0, 0, size, size);

  fill(255);
  // BLANK[2] (hint: 白い十字を描くには rect を二つ描こう)
  rect(d*3,0,d,size);
  rect(0,d*3,size,d);
}

