// テキスト「関数を作る(1) 引数のある関数」
// 練習問題：星を描く関数を改造して正N角形を描画する関数を作ってみよう

function setup(){
  createCanvas(300, 100);
  background(200);
  fill(0);
  crossmark(10, 10, 90, 90);
  ngmark(150, 50, 80);
  regularPolygon(250, 50, 40,12);
}

function crossmark(x1, y1, x2, y2){
  line(x1, y1, x2, y2);
  line(x2, y1, x1, y2);
}

function ngmark(cx, cy, r){
  push();
  noFill();
  strokeWeight(r * 0.1);
  let d = sqrt(r * r / 8);
  ellipse(cx, cy, r);
  line(cx - d, cy - d, cx + d, cy + d);
  pop();
}

function regularPolygon(cx, cy, r, n){
  beginShape();
  for(var i = 0; i < n; i++){
    let theta = TWO_PI * i * 1 / n - HALF_PI;
    let x = cx + cos(theta) * r;
    let y = cy + sin(theta) * r;
    vertex(x,y);
  }
  endShape(CLOSE);
}

//　テキスト「オブジェクト」
// 練習問題：ボールのサイズをランダムに変更してみよう
// 練習問題：何も操作しなくてもボールが湧いてくる機能を追加しよう

let balls;
let lasttime=0;

function setup(){
  createCanvas(windowWidth, windowHeight);
  balls = [];//??
  framerate(20);
}

function draw(){
  if(frameCount-lasttime>300){
    const b={
      x:random(10,100),
      y:random(10,100),
      size:50,
      vx:random(-5,5),
      vy:random(-5,5)
    };
    balls.push(b);
    lasttime=frameCount;
  }
  background(160, 192, 255);
  for(let i = 0; i < balls.length; i++){
    let b = balls[i];
    ellipse(b.x, b.y, b.size);
    b.x += b.vx;
    b.y += b.vy;
  }
}

function mouseDragged(){
  const dx = mouseX - pmouseX;
  const dy = mouseY - pmouseY;
  let size=random(30,200);
  if(mag(dx, dy) > 5){// mag(x,y) はベクトル(x,y)の長さ
    const b = { x: mouseX, y: mouseY, size, vx: dx, vy: dy };
    balls.push(b);//配列に追加
  }
}

function windowResized(){
  resizeCanvas(windowWidth, windowHeight);
}

