  for (let i = 0; i < balloons.length; i++) {
    let b = balloons[i];
      if((mouseX>b.x-35&&mouseX<b.x+35)&&(mouseY>b.y-55&&mouseY<b.y+15)){
        balloons.splice(i,1);
         }
   }
}

